def send_mail(email, title, content, filename=None):
  msg = EmailMessage()
  msg['From'] = USERNAME
  msg['To'] = email
  msg['Subject'] = title
  msg.set_content(content)

  if filename:
    # with open(filename, 'rb') as file:
    #   msg.add_attachment(file.read(), maintype='application', subtype='octet-stream', filename=filename)
    #   msg.add_header('Content-Disposition', 'attachment', filename=filename)

    filenames = filename.split(';')
    # filenames = filename.split(',')
    for filename in filenames:
      with open(filename, 'rb') as file:
        msg.add_attachment(file.read(), maintype='application', subtype='octet-stream', filename=filename)
        msg.add_header('Content-Disposition', 'attachment', filename=filename)

  with smtplib.SMTP_SSL('smtp.gmail.com', 465) as smtp:
    smtp.login(USERNAME, PASSWORD)
    smtp.send_message(msg)
  
  print(f"전송완료: <{email}>")

for index, row in my_emails.iterrows():
  send_mail(row['Email'], row['Title'], row['Content'], None if pd.isna(row['File']) else row['File'])